import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      data: []  };

}




delta = () =>  {
  fetch('http://localhost:8080')
  .then(res => res.json())
  .then(data => this.setState({data}));
                } 


  render() {
    return (
      <div>
      <button onClick={this.delta.bind(this)}>+</button>
      <div>{this.state.data}</div> 
      </div>
      ); 

  }
}

export default App;
